mapper.select:
SELECT 
FROM knowledge_node
WHERE node_type = #{nodeType}
ORDER BY reference_count DESC, created_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT FROM knowledge_node WHERE (name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
AND node_type = #{nodeType}
ORDER BY reference_count DESC, created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT DISTINCT n.*
FROM knowledge_node n
WHERE n.id IN (
    SELECT target_node_id FROM knowledge_edge WHERE source_node_id = #{nodeId}
    UNION
    SELECT source_node_id FROM knowledge_edge WHERE target_node_id = #{nodeId}
)
LIMIT 100

mapper.update:
UPDATE knowledge_node 
SET reference_count = reference_count + 1, updated_at = NOW()
WHERE id = #{id}

UPDATE knowledge_node 
SET embedding_vector = #{embeddingVector}, updated_at = NOW()
WHERE id = #{id}
